import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';

// tslint:disable-next-line:max-line-length
const CIRCULAR = 'data:image/svg+xml;charset=utf-8;base64,';
// tslint:disable-next-line:max-line-length
const STRAIGHT = 'data:image/svg+xml;charset=utf-8;base64,';

@Component({
  selector: 'vcl-busy-indicator',
  templateUrl: 'busy-indicator.component.html'
})
export class BusyIndicatorComponent implements OnChanges {
  constructor(private sanitizer: DomSanitizer ) { }

  @Input()
  type: 'straight' | 'circular' = 'circular';

  @Input()
  layout: 'vertical' | 'horizontal' = 'vertical';

  @Input()
  label = 'Loading';

  @Input()
  iconHeight = '3em';

  @Input()
  iconWidth = '3em';

  @Input()
  classSrc?: string;

  @Input()
  imageSrc?: string;

  indicatorSrc?: SafeResourceUrl = this.sanitizer.bypassSecurityTrustResourceUrl(CIRCULAR);

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.imageSrc || changes.type) {
      if (!this.imageSrc) {
        this.indicatorSrc = this.sanitizer.bypassSecurityTrustResourceUrl(this.type === 'straight' ? STRAIGHT : CIRCULAR);
      } else {
        this.indicatorSrc = this.imageSrc;
      }
    }
  }
}
